sys_variable_value.value:
//If we cannot find group on the Assignment Matrix table and it is not found in the manually assign script then set default group based on Case Type

setDefaultGroup(current.u_case_type);

function setDefaultGroup(x){
	switch(x){	
		case 'Benefits':
			current.assignment_group = 'e9e863a14ff84300548be28d0210c74d'; //Benefits Triage
			break;
			
		case 'Educational Assistance':
			current.assignment_group = '272aeb254ff84300548be28d0210c7e4'; //Educational Assistance
			break;
			
		case 'Leave of Absence COL':
			current.assignment_group = '6dd9abe14ff84300548be28d0210c794'; //LOA Triage
			break;	

		case 'Leave of Absence NCOL':
			current.assignment_group = '6dd9abe14ff84300548be28d0210c794'; //LOA Triage
			break;	

		case 'Retirement':
			current.assignment_group = '7b196fa14ff84300548be28d0210c774'; //Retirement Triage
			break;
			
		case 'Other': 
			current.assignment_group = 'e9e863a14ff84300548be28d0210c74d'; //Benefits Triage
			break;
		
		default:
			current.assignment_group = 'e9e863a14ff84300548be28d0210c74d'; //Benefits Triage
			
	}
	
		
}

