var n = workflow.scratchpad.title.startsWith('MD-');
var o = workflow.scratchpad.title.startsWith('DO-');
var retVal = null;

workflow.scratchpad.doctorGroupFound = 'false';
//Check if Title starts with MD or DO
if(n == true || o == true ){	
	workflow.scratchpad.doctorGroupFound = lookupGroup(workflow.scratchpad.caseType);
	
}

function lookupGroup(x){
	switch(x){
		case 'Benefits':
			current.assignment_group = '391667e54fb84300548be28d0210c7c9'; //Benefits Group 5
			retVal = 'true';
			break;
			
		case 'Leave of Absence COL':
			current.assignment_group = 'a4c923254ff84300548be28d0210c7e1'; //LOA Group 5
			retVal = 'true';
			break;	
			
		case 'Leave of Absence NCOL':
			current.assignment_group = 'a4c923254ff84300548be28d0210c7e1'; //LOA Group 5
			retVal = 'true';
			break;
			
		default :
			retVal = 'false';
	}
return retVal;
		
}

