workflow.scratchpad.caseType = current.u_case_type;
workflow.scratchpad.caseSubType = current.u_case_sub_type;
workflow.scratchpad.physician = current.u_is_physician;
workflow.scratchpad.title = current.u_caller.title;

//Parse out Cost Center
if(current.u_caller_organization){
	var orgArray = current.u_caller_organization.split('-');
	workflow.scratchpad.org = orgArray[0];
}

workflow.scratchpad.caseType = current.u_case_type;
workflow.scratchpad.caseSubType = current.u_case_sub_type;
workflow.scratchpad.physician = current.u_is_physician;
workflow.scratchpad.title = current.u_caller.title;

//Parse out Cost Center
if(current.u_caller_organization){
	var orgArray = current.u_caller_organization.split('-');
	workflow.scratchpad.org = orgArray[0];
}

workflow.scratchpad.lookupGroupFound = 'false';
workflow.scratchpad.lookupGroupFound = lookupGroup();


function lookupGroup(){
	//Look for a match on the Assignment Matrix table
	var grAssign = new GlideRecord('x_inthe_hr_case_mg_assignment_matrix');
	grAssign.addQuery('u_case_type', workflow.scratchpad.caseType);
	grAssign.addQuery('u_case_sub_type', workflow.scratchpad.caseSubType);
	grAssign.addQuery('u_organization', workflow.scratchpad.org);
	grAssign.addQuery('u_physician', workflow.scratchpad.physician);
	grAssign.query();

	//If a match is found then assign to group
	if(grAssign.next()){
		current.assignment_group = grAssign.u_assignment_group;
		return 'true';
		
	} else {
		return 'false';
	}
}

workflow.scratchpad.lookupGroupFound = 'false';
