//Determine which group to assign to
workflow.scratchpad.manualGroupFound = 'false';

workflow.scratchpad.manualGroupFound = findGroup();




function findGroup(){
	//*******************************************
	//Case Type = Benefits
	//Physician = true
	//Group = Benefits Group 5
	if(workflow.scratchpad.caseType == 'Benefits' && workflow.scratchpad.physician == 'true'){
		current.assignment_group = '391667e54fb84300548be28d0210c7c9'; //Benefits Group 5
		return 'true';
	}

	//*******************************************	
	//Case Type = Benefits
	//Case Sub-Type = Open Enrollment
	//Case Sub-Type = New Hire/Newly Eligible
		//Case Sub-Type = HSA/FSA
	//Case Sub-Type = PTO/Time Off
	//Case Sub-Type = Other
	//Physician = false
	//Group = Benefits Triage
	if(workflow.scratchpad.caseType == 'Benefits' && (workflow.scratchpad.caseSubType == 'Open Enrollment' || workflow.scratchpad.caseSubType == 'New Hire_Newly Eligible' || workflow.scratchpad.caseSubType == 'PTO_Time Off' || workflow.scratchpad.caseSubType == 'HSA_FSA' || workflow.scratchpad.caseSubType == 'Other') && workflow.scratchpad.physician == 'false'){
	current.assignment_group = 'e9e863a14ff84300548be28d0210c74d'; //Benefits Triage
	return 'true';
	}

	//*******************************************	
	//Case Type = Benefits
	//Case Sub-Type = Benefits Appeal
	//Case Sub-Type = New Hire/Newly Eligible
	//Case Sub-Type = PTO/Time Off
	//Case Sub-Type = HSA/FSA
	//Physician = N/A
	//Group = Benefits Escalation
	if(workflow.scratchpad.caseType == 'Benefits' && workflow.scratchpad.caseSubType == 'Benefits Appeal'){
	current.assignment_group = 'ed696fa14ff84300548be28d0210c7ee'; //Benefits Escalation
	return 'true';
	}	
		
	//*******************************************	
	//Case Type = Educational Assistance
	//Case Sub-Type = Check Request Form Status
	//Case Sub-Type = Elgibility
	//Case Sub-Type = Submit Education Plan
	//Case Sub-Type = Submit Request Form
	//Case Sub-Type = Other
	//Physician = N/A
	//Group = Educational Assistance
	if(workflow.scratchpad.caseType == 'Educational Assistance' && (workflow.scratchpad.caseSubType == 'Check Request Form Status' || workflow.scratchpad.caseSubType == 'Eligibility' || workflow.scratchpad.caseSubType == 'Submit Education Plan' || workflow.scratchpad.caseSubType == 'Submit Request Form' || workflow.scratchpad.caseSubType == 'Other')){
	current.assignment_group = '272aeb254ff84300548be28d0210c7e4'; //Educational Assistance
	return 'true';
	}	

	//*******************************************	
	//Case Type = Leave of Absence (Currently on Leave)
	//Case Type = Leave of Absence (NOT Currently on Leave)
	//Case Sub-Type = N/A
	//Physician = true
	//Group = LOA Group 5
	if((workflow.scratchpad.caseType == 'Leave of Absence COL' || workflow.scratchpad.caseType == 'Leave of Absence NCOL') && workflow.scratchpad.physician == 'true'){
		current.assignment_group = 'a4c923254ff84300548be28d0210c7e1'; //LOA Group 5
		return 'true';
	}
	
	//*******************************************	
	//Case Type = Leave of Absence (NOT Currently on Leave)
	//Case Sub-Type = Check Application Status
	//Case Sub-Type = Eligibility Policy
	//Case Sub-Type = Submit Application
	//Case Sub-Type = Other
	//Physician = false
	//Group = LOA Triage
	if(workflow.scratchpad.caseType == 'Leave of Absence NCOL' && (workflow.scratchpad.caseSubType == 'Check Application Status' || workflow.scratchpad.caseSubType == 'Eligibility_Policy' || workflow.scratchpad.caseSubType == 'Submit Application' || workflow.scratchpad.caseSubType == 'Other') && workflow.scratchpad.physician == 'false'){
		current.assignment_group = '6dd9abe14ff84300548be28d0210c794'; //LOA Triage
		return 'true';
	}
	
	//*******************************************	
	//Case Type = Retirement
	//Case Sub-Type = 401(k)/403(b)
	//Case Sub-Type = Begin Retirement Process
	//Case Sub-Type = Pension
	//Case Sub-Type = Retiree - General
	//Case Sub-Type = Retiree - Medical/Dental/Vision/Life
	//Case Sub-Type = Other
	//Physician = N/A
	//Group = Retirement Triage
	if(workflow.scratchpad.caseType == 'Retirement' && (workflow.scratchpad.caseSubType == '401_k_403_b' || workflow.scratchpad.caseSubType == 'Begin Retirement Process' || workflow.scratchpad.caseSubType == 'Pension' || workflow.scratchpad.caseSubType == 'Retiree - General' || workflow.scratchpad.caseSubType == 'Retiree Medical_Dental_Vision_Life' || workflow.scratchpad.caseSubType == 'Other')){
		current.assignment_group = '7b196fa14ff84300548be28d0210c774'; //Retirement Triage
		return 'true';
	}
	
	//*******************************************	
	//Case Type = Retirement
	//Case Sub-Type = Appeals
	//Physician = N/A
	//Group = Retirement Escalation
	if(workflow.scratchpad.caseType == 'Retirement' && workflow.scratchpad.caseSubType == 'Appeals'){
		current.assignment_group = '38a9abe14ff84300548be28d0210c7d2'; //Retirement Escalation
		return 'true';
	}
	
	//If no conditions are met then return false
	return 'false';

}

